{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[],"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code]\nimport numpy as np\nfrom sklearn.datasets import load_iris\nimport matplotlib.pyplot as plt\n\n# Utility Function\ndef plot_decision_region(X,y,model,resolution=0.02):\n    markers = ('o', 's', '^', 'v', '<')\n    colors = ('red', 'blue', 'lightgreen', 'gray', 'cyan')\n    cmap = ListedColormap(colors[:len(np.unique(y))])\n    x1_min,x1_max = X[:,0].min()-1, X[:,0].max()+1\n    x2_min,x2_max = X[:,1].min()-1, X[:,1].max()+1\n    xx1,xx2 = np.meshgrid(np.arange(x1_min,x1_max,resolution),np.arange(x2_min,x2_max,resolution))\n    lab = model.predict(np.array([xx1.ravel(),xx2.ravel()]).T)\n    lab = lab.reshape(xx1.shape)\n    plt.contourf(xx1, xx2, lab, alpha=0.3, cmap=cmap)\n    plt.xlim(xx1.min(), xx1.max())\n    plt.ylim(xx2.min(), xx2.max())\n    \n    for idx, cl in enumerate(np.unique(y)):\n        plt.scatter(x=X[y == cl, 0],y=X[y == cl, 1],alpha=0.8,c=colors[idx],\n                    marker=markers[idx],label=f'Class {cl}',edgecolor='black')\n        plt.legend()","metadata":{"_uuid":"e4f3179c-4b8e-4568-b41b-e64969063630","_cell_guid":"cb0e28da-5836-40dc-ad17-56a1e6d3eec9","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}